import React, { Component } from 'react';
import '../style/loginPrompt.css';
import ButtonStd from './btn-std.js';

class LoginPrompt extends Component {

    constructor(props) {
        super(props)

        this.state = {
            username: ""
        }
    }

    attempAuth() {
        const headers = new Headers();
        headers.append('Content-Type', 'Application/json');

        var authAttempt = new Request("http://wraughn.com:4005/users/login", {method: 'POST', body: '{"user": {"username":"' + this.refs.username.val + '", "password":"' + this.refs.password.val + '"}}'})
    }

    render() { 
        return (
            <div className="LoginPrompt">
                <div className="LoginBox">
                    <div className="header">Growth Quo</div>
                    <div className="authForm">
                        <input ref="username" type="text" placeholder="username" />
                        <input ref="password" type="password" placeholder="password"/>
                        <ButtonStd text="Login" status="inactive" clickFn={this.attempAuth}/>
                    </div>
                </div>

            </div>
        );
    }
}

export default LoginPrompt;
